{["internalId"]="6332",["level"]="50",["type"]="4",["repeatType"]="1",["displayType"]="0",["backgroundText"]="",["poiIndex"]="294967291",["isShareable"]="0"},
{["internalId"]="3219",["level"]="50",["type"]="7",["repeatType"]="1",["displayType"]="0",["backgroundText"]="I accepted a mission to help capture Arrius Keep for the Covenant.",["poiIndex"]="294967291",["isShareable"]="1"},
{["internalId"]="3509",["level"]="50",["type"]="0",["repeatType"]="0",["displayType"]="0",["backgroundText"]="A Red Rook messenger was captured near the Dwynnarth Ruins Lion Guard camp while he was on his way to a secret meeting between the Red Rooks and the Bloodthorn cult.",["poiIndex"]="39",["isShareable"]="1"},
{["internalId"]="3035",["level"]="50",["type"]="0",["repeatType"]="0",["displayType"]="0",["backgroundText"]="To stop the hags, I need to defeat the coven's leader, Mother Murk. Otherwise, other refugees will be in danger.",["poiIndex"]="2",["isShareable"]="1"},
{["internalId"]="3192",["level"]="50",["type"]="0",["repeatType"]="0",["displayType"]="0",["backgroundText"]="I met Harald Winvale, a repentant Bloodthorn cultist who is being haunted by ancestral spirits for desecrating his family tombs. He asked me to help him demonstrate that he still honors and respects his ancestors.",["poiIndex"]="294967291",["isShareable"]="1"},
