{["internalId"]="2703",["level"]="50",["type"]="7",["repeatType"]="1",["displayType"]="0",["backgroundText"]="I accepted a mission to scout Blue Road Mine for the Dominion.",["poiIndex"]="294967291",["isShareable"]="0"},
{["internalId"]="2676",["level"]="50",["type"]="7",["repeatType"]="1",["displayType"]="0",["backgroundText"]="I accepted a mission to scout Fort Aleswell for the Dominion.",["poiIndex"]="294967291",["isShareable"]="0"},
{["internalId"]="5602",["level"]="50",["type"]="7",["repeatType"]="0",["displayType"]="10",["backgroundText"]="My alliance is locked in an epic struggle for control of the Imperial City. The forces of Molag Bal hold the city in an iron grip, but enemy bannermen also vie for power. My alliance needs my help.",["poiIndex"]="294967291",["isShareable"]="1"},
{["internalId"]="2850",["level"]="50",["type"]="7",["repeatType"]="1",["displayType"]="0",["backgroundText"]="I accepted a mission to scout Blue Road Keep for the Pact.",["poiIndex"]="294967291",["isShareable"]="0"},
