{["internalId"]="3802",["level"]="50",["type"]="0",["repeatType"]="0",["displayType"]="0",["backgroundText"]="Pale-Heart wants me to take word of our accomplishments to Hissmir.",["poiIndex"]="294967291",["isShareable"]="1"},
{["internalId"]="4798",["level"]="50",["type"]="0",["repeatType"]="0",["displayType"]="10",["backgroundText"]="A shady character told me I was needed at the city of Arenthia. This Orthoron said someone of my prowess would come in handy.",["poiIndex"]="294967291",["isShareable"]="1"},
{["internalId"]="4795",["level"]="50",["type"]="0",["repeatType"]="0",["displayType"]="0",["backgroundText"]="A Bosmer scout claims her band was ambushed by Argoninan invaders. She managed to get away in the scuffle, but her allies are now captives of the Argonians.",["poiIndex"]="294967291",["isShareable"]="1"},
